import React, { Component } from "react";
import API from "../utils/API";
import UserContext from "../utils/userContext";
import CardContainer from "../components/CardContainer";
import Row from "../components/Row";

class Gallery extends Component {
  constructor(props) {
    super(props);
    this.state = {
      languages: [],
      user: {},
      users: [],
      userIndex: 0,
      capitalizeFirstLetter: this.capitalizeFirstLetter,
      handleBtnClick: this.handleBtnClick
    };
  }

  // When the component mounts, a call will be made to get random users.
  componentDidMount() {
    this.loadUsers();
  }

  capitalizeFirstLetter(string = "") {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }

  nextUser(userIndex) {
    // Ensure that the user index stays within our range of users
    if (userIndex >= this.state.users.length) {
      userIndex = 0;
    }
    this.setState({
      user: this.state.users[userIndex],
      userIndex: userIndex
    });
  }

  previousUser(userIndex) {
    // Ensure that the user index stays within our range of users
    if (userIndex < 0) {
      userIndex = this.state.users.length - 1;
    }
    this.setState({
      user: this.state.users[userIndex],
      userIndex: userIndex
    });
  }

  handleBtnClick = (event) => {
    // Get the title of the clicked button
    const btnName = event.target.getAttribute("data-value");
    if (btnName === "next") {
      const userIndex = this.state.userIndex + 1;
      this.nextUser(userIndex);
    } else {
      const userIndex = this.state.userIndex - 1;
      this.previousUser(userIndex);
    }
  };

  loadUsers = () => {
    API.getLanguagesList()
      .then(languages => {
        return API.getUsersByLanguage(languages[0]).then((users) => {
          return this.setState({
            languages: languages,
            users: users,
            user: users[0]
          });
        });
      })
      .catch(err => console.log(err));
  };

  render() {
    return (
      <UserContext.Provider value={this.state}>
        <div>
          <h1 className="text-center">Welcome to LinkedUp</h1>
          <h3 className="text-center">Click on the arrows to browse users</h3>
          <Row>
            <CardContainer />
          </Row>
        </div>
      </UserContext.Provider>
    );
  }
}

export default Gallery;
